class LoadMore{loadMoreCommentsEventOnclick(){const e=document.querySelector("#loadMore");let t=2;e.addEventListener("click",(e=>{const o=[...document.querySelectorAll(".user-comment")];for(let e=t;e<t+2;e++)o[e]&&(o[e].style.display="block");t+=2,t>=o.length&&(event.target.style.display="none")}))}addHeightAfterClick(){let e=document.querySelector(".main-product-cart"),t=e.offsetHeight+250;e.style.height=`${t}px`}addMarginTopForRecallBlock(){let e=document.querySelector(".call-back-block"),t=window.getComputedStyle(e,null).getPropertyValue("margin-top"),o=parseInt(t);o+=250,e.style.marginTop=`${o}px`}}let loadMoreComments=new LoadMore;loadMoreComments.loadMoreCommentsEventOnclick();