let slideIndex=1;class Slider{showSlides(e){let l=document.getElementsByClassName("main-product-img"),s=document.getElementsByClassName("product-scroller-img");e>l.length&&(slideIndex=1),e<1&&(slideIndex=l.length);for(let e=0;e<l.length;e++)l[e].style.display="none";for(let e=0;e<s.length;e++)s[e].className=s[e].className.replace(" active-product-scroller","");l[slideIndex].style.display="block",s[slideIndex-1].className+=" active-product-scroller"}currentSlide(e){this.showSlides(slideIndex=e)}nextSlide(e){this.showSlides(slideIndex+=e)}}let slider=new Slider;slider.showSlides(slideIndex);